draw(circle((0,0),1));
draw(circle((1,sqrt(3)),1));
draw(circle((-1,sqrt(3)),1));
draw(circle((-1,-sqrt(3)),1));
draw(circle((1,-sqrt(3)),1));
draw(circle((2,0),1));
draw(circle((-2,0),1));
[/asy]

